param
(
  [Parameter(Mandatory)]
  [string]$ResourceId,

  [Parameter()]
  [int]$TimeoutInSecods = 60
)
$DeploymentScriptOutputs = @{}
$sw = [System.Diagnostics.Stopwatch]::StartNew()
$synapse = Get-AzSynapseWorkspace -ResourceId $ResourceId -ErrorAction Stop
while ($synapse.Encryption.CustomerManagedKeyDetails.Status -ne 'Consistent') {
  if ($sw.Elapsed.TotalSeconds -lt $TimeoutInSecods) {
    Write-Information -MessageData "Waiting for: '$ResourceId' activation. Time: $($sw.Elapsed.TotalSeconds)/$TimeoutInSecods"
    Start-Sleep -Seconds 10
    $synapse = Get-AzSynapseWorkspace -ResourceId $ResourceId -ErrorAction Stop
  } else {
    throw "Timeout of: $TimeoutInSecods seconds reached"
  }
}

